package javaprg;

public class _23If_else {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10,b=30;
		if(a>b) {
			System.out.println("a is bigger than b");
		}else if(a<b) {
			System.out.println("b is bigger than a");
		}else {
			System.out.println("both are equal");
		}

	}

}
